// Wrapper component to fetch real achievement data
function AchievementSystemWrapper({ user, onAchievementUnlocked }: { 
  user: any; 
  onAchievementUnlocked?: (achievement: any) => void; 
}) {
  // Fetch all achievements
  const { data: achievements = [], isLoading: achievementsLoading } = useQuery({
    queryKey: ['/api/achievements'],
    enabled: !!user
  });

  // Fetch user achievements  
  const { data: userAchievements = [], isLoading: userAchievementsLoading } = useQuery({
    queryKey: ['/api/achievements/me'],
    enabled: !!user
  });

  // Fetch user study sessions for stats calculation
  const { data: sessions = [], isLoading: sessionsLoading } = useQuery({
    queryKey: ['/api/sessions/me'],
    enabled: !!user
  });

  if (achievementsLoading || userAchievementsLoading || sessionsLoading) {
    return (
      <div className="space-y-4">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-64 mb-4"></div>
          <div className="grid grid-cols-3 gap-4">
            {[1, 2, 3].map(i => (
              <div key={i} className="h-32 bg-gray-200 rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  // Type cast and calculate real user stats from sessions and user data
  const typedSessions = (sessions as any[]) || [];
  const totalStudyTime = typedSessions.reduce((total: number, session: any) => 
    total + (session.duration_minutes || 0), 0
  );

  const userStats = {
    totalXP: user.total_xp || 0,
    level: user.study_level || 1,
    streak: user.study_streak || 0,
    studyTime: totalStudyTime,
    sessionsCompleted: typedSessions.length,
    friendsCount: 0, // TODO: Add friends feature
    tutorSessions: 0 // TODO: Add tutor sessions tracking
  };

  // Type cast and map backend achievements to frontend format
  const typedAchievements = (achievements as any[]) || [];
  const typedUserAchievements = (userAchievements as any[]) || [];

  const mappedAchievements = typedAchievements.map((achievement: any) => {
    const isUnlocked = typedUserAchievements.some((ua: any) => ua.achievement_id === achievement.id);
    const userAchievement = typedUserAchievements.find((ua: any) => ua.achievement_id === achievement.id);

    return {
      id: achievement.id,
      title: achievement.name,
      description: achievement.description,
      icon: getAchievementIcon(achievement.icon),
      category: mapCategory(achievement.category),
      rarity: mapRarity(achievement.category),
      points: achievement.xp_reward || 0,
      requirements: achievement.unlock_criteria || { type: 'unknown', target: 1 },
      earned: isUnlocked,
      earnedDate: userAchievement?.unlocked_at || undefined,
      isNew: false
    };
  });

  return (
    <AchievementSystem
      achievements={mappedAchievements}
      userStats={userStats}
      onAchievementUnlocked={onAchievementUnlocked}
    />
  );
}

// Helper functions
function getAchievementIcon(iconName: string): string {
  const iconMap: { [key: string]: string } = {
    'target': 'ðŸŽ¯',
    'flame': 'ðŸ”¥', 
    'sunrise': 'ðŸŒ…',
    'star': 'â­',
    'clock': 'â°',
    'trophy': 'ðŸ†',
    'book': 'ðŸ“š',
    'heart': 'â¤ï¸'
  };
  return iconMap[iconName] || 'ðŸ†';
}

function mapCategory(backendCategory: string): 'study' | 'social' | 'streak' | 'milestone' | 'special' {
  const categoryMap: { [key: string]: 'study' | 'social' | 'streak' | 'milestone' | 'special' } = {
    'milestone': 'milestone',
    'streak': 'streak', 
    'focus': 'study',
    'xp': 'milestone',
    'social': 'social'
  };
  return categoryMap[backendCategory] || 'milestone';
}

function mapRarity(category: string): 'common' | 'rare' | 'epic' | 'legendary' {
  // Simple mapping based on category - could be enhanced with more logic
  const rarityMap: { [key: string]: 'common' | 'rare' | 'epic' | 'legendary' } = {
    'milestone': 'common',
    'streak': 'rare',
    'focus': 'epic', 
    'xp': 'legendary',
    'social': 'rare'
  };
  return rarityMap[category] || 'common';
}

export default App;

