  { id: 'fun', name: 'Make Learning Fun', icon: 'ðŸŽ‰', description: 'Enjoy the learning process' },
  { id: 'friends', name: 'Study with Friends', icon: 'ðŸ‘¥', description: 'Learn together with peers' }
];

const studyStyles = [
  { 
