import { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { 
  Search, 
  BookOpen, 
  Volume2, 
  X, 
  AlertCircle,
  ExternalLink
} from "lucide-react";

interface DictionaryEntry {
  word: string;
  phonetic?: string;
  meanings: Meaning[];
  sourceUrls: string[];
}

interface Meaning {
  partOfSpeech: string;
  definitions: Definition[];
  synonyms: string[];
  antonyms: string[];
}

interface Definition {
  definition: string;
  example?: string;
  synonyms: string[];
  antonyms: string[];
}

interface DictionaryLookupProps {
  isOpen: boolean;
  onClose: () => void;
  initialWord?: string;
}

export function DictionaryLookup({ isOpen, onClose, initialWord = "" }: DictionaryLookupProps) {
  const [searchWord, setSearchWord] = useState(initialWord);
  const [result, setResult] = useState<DictionaryEntry | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const searchDictionary = async (word: string) => {
    if (!word.trim()) return;

    setIsLoading(true);
    setError(null);
    setResult(null);

    try {
      const response = await fetch(`https://api.dictionaryapi.dev/api/v2/entries/en/${encodeURIComponent(word.trim())}`);

      if (!response.ok) {
        throw new Error('Word not found');
      }

      const data = await response.json();
      if (data && data.length > 0) {
        setResult(data[0] as DictionaryEntry);
      } else {
        throw new Error('No definition found');
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch definition');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSearch = () => {
    searchDictionary(searchWord);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  const playPronunciation = (text: string) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.rate = 0.8;
      utterance.pitch = 1;
      window.speechSynthesis.speak(utterance);
    }
  };

  if (!isOpen) return null;

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="fixed inset-0 bg-black/50 flex items-center justify-center z-50"
      data-testid="dictionary-overlay"
    >
      <motion.div
        initial={{ scale: 0.9, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.9, opacity: 0 }}
        className="w-full max-w-2xl max-h-[90vh] m-4"
      >
        <Card className="shadow-2xl">
          <CardHeader className="border-b">
            <div className="flex items-center justify-between">
              <CardTitle className="flex items-center gap-2">
                <BookOpen className="w-5 h-5 text-blue-600" />
                Dictionary Lookup
              </CardTitle>
              <Button
                variant="ghost"
                size="sm"
                onClick={onClose}
                data-testid="button-close-dictionary"
              >
                <X className="w-4 h-4" />
              </Button>
            </div>
          </CardHeader>

          <CardContent className="p-6">
            {/* Search Input */}
            <div className="flex gap-2 mb-6">
              <Input
                placeholder="Enter a word to look up..."
                value={searchWord}
                onChange={(e) => setSearchWord(e.target.value)}
                onKeyPress={handleKeyPress}
                className="flex-1"
                data-testid="input-dictionary-search"
              />
              <Button 
                onClick={handleSearch}
                disabled={!searchWord.trim() || isLoading}
                data-testid="button-search-dictionary"
              >
                <Search className="w-4 h-4 mr-2" />
                {isLoading ? 'Searching...' : 'Search'}
              </Button>
            </div>

            {/* Results */}
            <div className="max-h-[60vh] overflow-y-auto">
              {isLoading && (
                <div className="space-y-4">
                  <Skeleton className="h-6 w-48" />
                  <Skeleton className="h-4 w-32" />
                  <div className="space-y-2">
                    <Skeleton className="h-4 w-full" />
                    <Skeleton className="h-4 w-full" />
                    <Skeleton className="h-4 w-3/4" />
                  </div>
                </div>
              )}

              {error && (
                <motion.div
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="flex items-center justify-center py-8"
                >
                  <div className="text-center">
                    <AlertCircle className="w-12 h-12 text-orange-500 mx-auto mb-2" />
                    <p className="text-gray-600 font-medium">{error}</p>
                    <p className="text-sm text-gray-500 mt-1">
                      Try checking the spelling or search for a different word.
                    </p>
                  </div>
                </motion.div>
              )}

              {result && (
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="space-y-6"
                  data-testid="dictionary-result"
                >
                  {/* Word Header */}
                  <div className="border-b pb-4">
                    <div className="flex items-center gap-3 mb-2">
                      <h2 className="text-3xl font-bold text-gray-800">
                        {result.word}
                      </h2>
                      {result.phonetic && (
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => playPronunciation(result.word)}
                          className="flex items-center gap-1 text-blue-600 hover:text-blue-700"
                          data-testid="button-pronunciation"
                        >
                          <Volume2 className="w-4 h-4" />
                          <span className="text-sm font-mono">{result.phonetic}</span>
                        </Button>
                      )}
                    </div>
                  </div>

                  {/* Meanings */}
                  <div className="space-y-6">
                    {result.meanings.map((meaning, meaningIndex) => (
                      <div key={meaningIndex} className="space-y-3">
                        <Badge variant="outline" className="mb-3">
                          {meaning.partOfSpeech}
                        </Badge>

                        <div className="space-y-4">
                          {meaning.definitions.map((definition, defIndex) => (
                            <div key={defIndex} className="pl-4 border-l-2 border-gray-200">
                              <p className="text-gray-800 leading-relaxed">
                                <span className="font-medium">{defIndex + 1}.</span> {definition.definition}
                              </p>

                              {definition.example && (
                                <p className="text-gray-600 italic mt-2 text-sm">
                                  <strong>Example:</strong> "{definition.example}"
                                </p>
                              )}

                              {(definition.synonyms.length > 0 || meaning.synonyms.length > 0) && (
                                <div className="mt-2">
                                  <span className="text-sm font-medium text-green-700">Synonyms: </span>
                                  <span className="text-sm text-green-600">
                                    {[...definition.synonyms, ...meaning.synonyms]
                                      .slice(0, 5)
                                      .join(', ')}
                                  </span>
                                </div>
                              )}

                              {(definition.antonyms.length > 0 || meaning.antonyms.length > 0) && (
                                <div className="mt-1">
                                  <span className="text-sm font-medium text-red-700">Antonyms: </span>
                                  <span className="text-sm text-red-600">
                                    {[...definition.antonyms, ...meaning.antonyms]
                                      .slice(0, 5)
                                      .join(', ')}
                                  </span>
                                </div>
                              )}
                            </div>
                          ))}
                        </div>
                      </div>
                    ))}
                  </div>

                  {/* Source */}
                  {result.sourceUrls && result.sourceUrls.length > 0 && (
                    <div className="border-t pt-4">
                      <div className="flex items-center gap-2 text-sm text-gray-500">
                        <ExternalLink className="w-4 h-4" />
                        <span>Source: </span>
                        <a
                          href={result.sourceUrls[0]}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="text-blue-600 hover:text-blue-700 underline"
                        >
                          Free Dictionary API
                        </a>
                      </div>
                    </div>
                  )}
                </motion.div>
              )}
            </div>
          </CardContent>
        </Card>
      </motion.div>
    </motion.div>
  );
}

client/src/components/PDFViewer.tsx.content:
import { useState } from "react";
import { Document, Page, pdfjs } from "react-pdf";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { 
  ChevronLeft, 
  ChevronRight, 
  ZoomIn, 
  ZoomOut, 
  RotateCw, 
  Download,
  Maximize2,
  FileText
} from "lucide-react";
import { motion } from "framer-motion";

// Set up PDF.js worker with locked version for security
// Using specific version to prevent supply-chain attacks
const PDF_JS_VERSION = "3.11.174"; // Lock to specific version
pdfjs.GlobalWorkerOptions.workerSrc = `https://cdnjs.cloudflare.com/ajax/libs/pdf.js/${PDF_JS_VERSION}/pdf.worker.min.js`;

interface PDFViewerProps {
  file: File;
  onClose?: () => void;
  className?: string;
}

interface PDFViewerState {
  numPages: number | null;
  pageNumber: number;
  scale: number;
  rotation: number;
}

export function PDFViewer({ file, onClose, className = "" }: PDFViewerProps) {
  const [state, setState] = useState<PDFViewerState>({
    numPages: null,
    pageNumber: 1,
    scale: 1.2,
    rotation: 0
  });
  const [pageInputValue, setPageInputValue] = useState("1");
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const onDocumentLoadSuccess = ({ numPages }: { numPages: number }) => {
    setState(prev => ({ ...prev, numPages }));
    setIsLoading(false);
    setError(null);
  };

  const onDocumentLoadError = () => {
    setError("Failed to load PDF. Please try another file.");
    setIsLoading(false);
  };

  const changePage = (offset: number) => {
    setState(prev => ({
      ...prev,
      pageNumber: Math.max(1, Math.min(prev.pageNumber + offset, prev.numPages || 1))
    }));
  };

  const goToPage = () => {
    const page = parseInt(pageInputValue);
    if (page && page > 0 && page <= (state.numPages || 1)) {
      setState(prev => ({ ...prev, pageNumber: page }));
    }
    setPageInputValue(state.pageNumber.toString());
  };

  const changeScale = (scaleDelta: number) => {
    setState(prev => ({
      ...prev,
      scale: Math.max(0.5, Math.min(prev.scale + scaleDelta, 3.0))
    }));
  };

  const rotate = () => {
    setState(prev => ({
      ...prev,
      rotation: (prev.rotation + 90) % 360
    }));
  };

  const downloadFile = () => {
    const url = URL.createObjectURL(file);
    const a = document.createElement('a');
    a.href = url;
    a.download = file.name;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  return (
    <Card className={`flex flex-col h-full ${className}`} data-testid="pdf-viewer">
      <CardHeader className="py-3 border-b">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <FileText className="w-5 h-5 text-blue-600" />
            <CardTitle className="text-lg truncate max-w-[200px]" title={file.name}>
              {file.name}
            </CardTitle>
          </div>

          <div className="flex items-center gap-2">
            <Badge variant="outline">
              {(file.size / 1024 / 1024).toFixed(1)} MB
            </Badge>
            {onClose && (
              <Button variant="ghost" size="sm" onClick={onClose} data-testid="button-close-pdf">
                âœ•
              </Button>
            )}
          </div>
        </div>
      </CardHeader>

      <CardContent className="flex-1 flex flex-col p-0">
        {/* Controls */}
        <div className="flex items-center justify-between p-3 border-b bg-gray-50">
          <div className="flex items-center gap-2">
            <Button
              variant="outline"
              size="sm"
              onClick={() => changePage(-1)}
              disabled={state.pageNumber <= 1}
              data-testid="button-prev-page"
            >
              <ChevronLeft className="w-4 h-4" />
            </Button>

            <div className="flex items-center gap-2">
              <Input
                type="number"
                value={pageInputValue}
                onChange={(e) => setPageInputValue(e.target.value)}
                onBlur={goToPage}
                onKeyDown={(e) => e.key === 'Enter' && goToPage()}
                className="w-16 h-8 text-center"
                min="1"
                max={state.numPages || 1}
                data-testid="input-page-number"
              />
              <span className="text-sm text-gray-600">
                / {state.numPages || 0}
              </span>
            </div>

            <Button
              variant="outline"
              size="sm"
              onClick={() => changePage(1)}
              disabled={state.pageNumber >= (state.numPages || 1)}
              data-testid="button-next-page"
            >
              <ChevronRight className="w-4 h-4" />
            </Button>
          </div>

          <div className="flex items-center gap-1">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => changeScale(-0.2)}
              disabled={state.scale <= 0.5}
              data-testid="button-zoom-out"
            >
              <ZoomOut className="w-4 h-4" />
            </Button>

            <Badge variant="outline" className="mx-1">
              {Math.round(state.scale * 100)}%
            </Badge>

            <Button
              variant="ghost"
              size="sm"
              onClick={() => changeScale(0.2)}
              disabled={state.scale >= 3.0}
              data-testid="button-zoom-in"
            >
              <ZoomIn className="w-4 h-4" />
            </Button>

            <Button
              variant="ghost"
              size="sm"
              onClick={rotate}
              data-testid="button-rotate"
            >
              <RotateCw className="w-4 h-4" />
            </Button>

            <Button
              variant="ghost"
              size="sm"
              onClick={downloadFile}
              data-testid="button-download-pdf"
            >
              <Download className="w-4 h-4" />
            </Button>
          </div>
        </div>

        {/* PDF Content */}
        <div className="flex-1 overflow-auto bg-gray-100">
          {isLoading && (
            <div className="flex items-center justify-center h-full">
              <motion.div
                animate={{ rotate: 360 }}
                transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                className="w-8 h-8 border-2 border-blue-600 border-t-transparent rounded-full"
              />
              <span className="ml-3 text-gray-600">Loading PDF...</span>
            </div>
          )}

          {error && (
            <div className="flex items-center justify-center h-full">
              <div className="text-center">
                <FileText className="w-12 h-12 text-red-500 mx-auto mb-2" />
                <p className="text-red-600 font-medium">{error}</p>
              </div>
            </div>
          )}

          {!isLoading && !error && (
            <div className="flex justify-center p-4">
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="shadow-lg"
              >
                <Document
                  file={file}
                  onLoadSuccess={onDocumentLoadSuccess}
                  onLoadError={onDocumentLoadError}
                  loading=""
                >
                  <Page
                    pageNumber={state.pageNumber}
                    scale={state.scale}
                    rotate={state.rotation}
                    renderTextLayer={false}
                    renderAnnotationLayer={false}
                  />
                </Document>
              </motion.div>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/utils/security.ts.content:
// Security utilities for URL validation and iframe safety

const ALLOWED_YOUTUBE_DOMAINS = [
  'www.youtube.com',
  'youtube.com',
  'youtu.be',
  'www.youtube-nocookie.com',
  'youtube-nocookie.com'
];

const ALLOWED_SPOTIFY_DOMAINS = [
  'open.spotify.com',
  'embed.spotify.com'
];

/**
 * Validates and normalizes YouTube URLs to ensure they're safe for embedding
 */
export function validateYouTubeURL(url: string): string | null {
  try {
    const urlObj = new URL(url);

    // Check if domain is allowed
    if (!ALLOWED_YOUTUBE_DOMAINS.includes(urlObj.hostname)) {
      console.warn('Invalid YouTube domain:', urlObj.hostname);
      return null;
    }

    // Convert to embed format if it's a watch URL
    if (urlObj.pathname === '/watch' && urlObj.searchParams.get('v')) {
      const videoId = urlObj.searchParams.get('v');
      return `https://www.youtube-nocookie.com/embed/${videoId}?enablejsapi=0&modestbranding=1&rel=0`;
    }

    // If it's already an embed URL, ensure it uses the secure domain
    if (urlObj.pathname.startsWith('/embed/')) {
      const videoId = urlObj.pathname.split('/embed/')[1];
      return `https://www.youtube-nocookie.com/embed/${videoId}?enablejsapi=0&modestbranding=1&rel=0`;
    }

    // Handle youtu.be short URLs
    if (urlObj.hostname === 'youtu.be') {
      const videoId = urlObj.pathname.substring(1);
      return `https://www.youtube-nocookie.com/embed/${videoId}?enablejsapi=0&modestbranding=1&rel=0`;
    }

    return null;
  } catch (error) {
    console.warn('Invalid YouTube URL format:', url);
    return null;
  }
}

/**
 * Validates and normalizes Spotify URLs to ensure they're safe for embedding
 */
export function validateSpotifyURL(url: string): string | null {
  try {
    const urlObj = new URL(url);

    // Check if domain is allowed
    if (!ALLOWED_SPOTIFY_DOMAINS.includes(urlObj.hostname)) {
      console.warn('Invalid Spotify domain:', urlObj.hostname);
      return null;
    }

    // Convert open.spotify.com URLs to embed format if needed
    if (urlObj.hostname === 'open.spotify.com') {
      // Extract the resource type and ID from the URL
      const pathMatch = urlObj.pathname.match(/\/(track|album|playlist|show|episode)\/([a-zA-Z0-9]+)/);
      if (pathMatch) {
        const [, type, id] = pathMatch;
        return `https://open.spotify.com/embed/${type}/${id}?utm_source=generator&theme=0`;
      }
    }

    // If it's already an embed URL, keep it as is
    if (urlObj.hostname === 'embed.spotify.com' || urlObj.pathname.startsWith('/embed/')) {
      return url;
    }

    return null;
  } catch (error) {
    console.warn('Invalid Spotify URL format:', url);
    return null;
  }
}

/**
 * Get minimal sandbox and allow attributes for media iframes
 */
export function getSecureIframeProps() {
  return {
    sandbox: 'allow-scripts allow-same-origin allow-presentation',
    allow: 'accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture',
    referrerPolicy: 'strict-origin-when-cross-origin' as const
  };
}

